import { ReplaySubject } from 'rxjs';
export class Util {}
export function callWithSnackBar(snackBar, httpCall, messages) {
  snackBar.open(messages[0], null, {
    duration: 30000
  });
  let returnValue = new ReplaySubject(1);
  httpCall.subscribe(success => {
    returnValue.next(success);
    returnValue.complete();
    snackBar.open(messages[1], null, {
      duration: 2000
    });
  }, error => {
    returnValue.complete();
    console.log(error);
    snackBar.open(messages[2], null, {
      duration: 4000,
      panelClass: ['snack-bar-error']
    });
  });
  return returnValue;
}
